function initannuaire(){var e=$("#searchAnnuaire").val();$.ajax({url:"http://udamobile.u-clermont1.fr/v2/annuaire/searchpage.php",type:"POST",data:{name:e},cache:true,success:function(e){makelist(e)}})}function makelist(e){var t="",n,r,s;if(e!=""){n=jQuery.isPlainObject(e)?e:jQuery.parseJSON(e);r=n.count;if(r==0){t+='<li class="noneResultAnnuaire">Aucun résultat pour la recherche demandée.</li>'}else{s=n.exact;for(i=0;i<r;i++){t+='<li class="resultAnnuaire"><h3>'+s[i].nom+" "+s[i].prenom+'</h3><img src="images/mail_green.png">Courriel : <p><a href="mailto:'+s[i].mail+'">'+s[i].mail+"</a></p>";if(s[i].tel){t+='<img src="images/phone.png">TEL :<a href="tel:'+s[i].tel+'">'+s[i].tel+"</a>"}t+="</li>"}}}else{t+='<li class="noneResultAnnuaire">Aucune résultat pour la recherche demandée.</li>'}$("#listAnnuaire").html(t)}